import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { addUser } from '../actions/actionCreators'
import { connect } from 'react-redux'

class Cadastrar extends Component {
  constructor() {
    super()
    this.state = {
      nome: '',
      idade: '',
      email: '',
      password: '',
    }

    this.changeState = this.changeState.bind(this)
    this.addNewUser = this.addNewUser.bind(this)
  }

  changeState({ target }) {
    const { name, value } = target
    this.setState({ [name]: value })
  }

  addNewUser() {
    const { newUser } = this.props
    const { nome, idade, email } = this.state
    const user = { nome, idade, email }
    newUser(user)
  }

  render() {
    return (
      <div style={{ textAlign: 'center', marginTop: '115px' }}>
        <h1>Realizar cadastro</h1>
        <label>
          Nome:
          <input
            style={{ marginBottom: '10px' }}
            name="nome"
            onChange={this.changeState}
            type="text"
          />
        </label>
        <br />
        <label>
          Idade:
          <input
            style={{ marginBottom: '10px' }}
            name="idade"
            onChange={this.changeState}
            type="text"
          />
        </label>
        <br />
        <label>
          Email:
          <input
            style={{ marginBottom: '10px' }}
            name="email"
            onChange={this.changeState}
            type="text"
          />
        </label>
        <br />
        <label>
          Senha:
          <input
            style={{ marginBottom: '10px' }}
            name="password"
            onChange={this.changeState}
            type="password"
          />
        </label>
        <br />
        <button style={{ marginBottom: '10px' }} onClick={this.addNewUser}>
          Gerar cadastro
        </button>
        <br />
        <Link to="/cadastros">Clientes cadastrados</Link>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => ({
  newUser: (user) => dispatch(addUser(user)),
})

export default connect(null, mapDispatchToProps)(Cadastrar)
