import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

class Cadastrados extends Component {
  constructor(props) {
    super(props)
    const { users } = props
    this.state = {
      users: users,
    }
    this.orderUsers = this.orderUsers.bind(this)
  }

  orderUsers({ target: { checked } }) {
    const { users } = this.props
    if(checked) this.setState({users: [...users].sort((a,b) => a.nome.charCodeAt(0) - a.nome.charCodeAt(0))})
  }

  render() {
    return (
      <div style={{ textAlign: 'center', marginTop: '150px' }}>
        <input type="checkbox" onChange={this.orderUsers} />
        {users.length ? (
          users.map(({ nome, email }) => (
            <h3 key={email}>
              {nome}, {email}
            </h3>
          ))
        ) : (
          <h1 style={{ color: '#252525' }}>Nenhum cliente cadastrado</h1>
        )}
        <Link
          style={{
            color: '#eee',
            background: '#252525',
            padding: '4px',
            borderRadius: '5px',
            textDecoration: 'none',
          }}
          to="/cadastrar"
        >
          Cadastrar
        </Link>
      </div>
    )
  }
}

const mapStateToProps = ({ userReducer }) => ({
  users: userReducer,
})

export default connect(mapStateToProps, null)(Cadastrados)
