import { ADD_USER, DELETE_USER } from '../actions/actionCreators'

export default function userReducer(state = [], action) {
  switch (action.type) {
    case ADD_USER:
      return [...state, action.user]
    case DELETE_USER:
      console.log(state)
      console.log(action)
      return [...state].filter((item) => {
        console.log(item)
        return item.name !== action.user
      })
    default:
      return state
  }
}
