import { ADD_USER, DELETE_USER } from '../actions/actionCreators'

export default function userReducer(state = [], action) {
  switch (action.type) {
    case ADD_USER:
      return [...state, action.user]
    case DELETE_USER:
      const value = [...state].filter((item) => item.nome !== action.user)
      console.log(value)
      return value
    default:
      return state
  }
}
